mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
select d.id, d.name, d.category_id as categoryId, c.name as categoryName, d.status, d.price, d.image, d.description, d.update_time as updateTime from dish d left join category c on d.category_id = c.id
and d.name like concat('%', #{dto.name}, '%')
and d.category_id = #{dto.categoryId}
and d.status = #{dto.status}
order by c.update_time desc, d.id
