mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.create_time, s.update_time, c.name as category_name from setmeal s left join category c on s.category_id = c.id
and s.name LIKE CONCAT('%', #{dto.name}, '%')
and s.category_id = #{dto.categoryId}
and s.status = #{dto.status}
order by s.update_time desc

mapper.update:
UPDATE setmeal
name = #{name}, category_id = #{categoryId}, image = #{image}, price = #{price}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}
